import React , { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { projects } from '../data/projectsData'; // Import your projects data here

function Project() {
    const { projectId } = useParams();
    const project = projects.find((p) => p.id === parseInt(projectId));

    if (!project) {
      return <div>Project not found</div>;
    }

    const [showSidebar, setShowSidebar] = useState(false);
    const [sidebarTop, setSidebarTop] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const firstSection = document.getElementById(project.sections[0].id);
      if (firstSection) {
        setShowSidebar(window.scrollY >= firstSection.offsetTop - 100);
        setSidebarTop(firstSection.offsetTop);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [project.sections]);

    return (
      <div className="flex flex-col md:flex-row">

        <nav className="bg-gray-200 md:w-1/4">
        {showSidebar && (
          <ul className="p-4 space-y-2 md:space-y-4" style={{ top: `${sidebarTop}px` }}>
            {project.sections.map((section) => (
              <li key={section.id}>
                <a href={`#${section.id}`} className="block p-2 hover:bg-blue-200">
                  {section.title}
                </a>
              </li>
            ))}
          </ul>
          )}
        </nav>

        {/* Main Content */}
        <div className="flex-1 p-4 md:p-8">
          <h1 className="text-3xl font-semibold mb-4">{project.title}</h1>
          <p className="text-gray-600 mb-6">{project.description}</p>
          {/* Sections */}
          {project.sections.map((section) => (
            <div key={section.id} id={section.id} className="mb-8">
              <h2 className="text-xl font-semibold mb-4">{section.title}</h2>
              <p>{section.content}</p>
            </div>
          ))}
        </div>
      </div>
    );
}

export default Project;
